import Hero from "./Hero";

// TMDB API key = 39df20a40f86a4c7ab69e94de9655ad9
// link = https://api.themoviedb.org/3/search/movie?api_key=39df20a40f86a4c7ab69e94de9655ad9&language=en-US&query=star%20wars&page=1&include_adult=false

const MovieCard = ({ movie }) => {
  const posterURL = `https://image.tmdb.org/t/p/w500/${movie.poster_path}`;
  return (
    <div className="container">
      <div className="row">

      </div>
    <div className="col-lg-3 col-md-3 col-2">
      <div className="card">
        <img
          src={posterURL}
          className="card-img-top"
          alt={movie.original_title}
        />
        <div className="card-body">
          <h5 className="card-title">{movie.original_title}</h5>
          <p className="card-text"></p>
          <a href="#" className="btn btn-primary">
            Show details
          </a>
        </div>
      </div>
    </div>
    </div>
  );
};

const SearchView = ({ keyword, searchResults }) => {
  const nothing = `Nothing found for ${keyword}`;
  const title = `You are searching for ${keyword}`;
  const searchHTML = searchResults.map((obj, i) => {
    return <MovieCard movie={obj} key={i} />;
  });
  if (searchHTML.length == 0) {
    searchHTML.push(
      <div>
        <p className="m-5 text-danger h5">{nothing}</p>
      </div>
    );
  }
  const results = searchHTML;
  return (
    <>
      <Hero text={title} />
      {results}
    </>
  );
};

export default SearchView;
